mapper.select:
select *
from sys_user
where user_name = #{userName}
  and user_password = #{userPassword}

select * from sys_user where 1 = 1
and user_name like concat('%', #{userName}, '%')
and user_password = #{userPassword}

select *
from sys_user
where 1 = 1


        and id = #{id}


        and user_name = #{userName}


        and 1 = 2
